import React, { Component } from 'react';
import axios from 'axios';

export default class AddCustomer extends  Component {
    constructor(props){
        super(props);

        this.state = {
            cust_email: '',
            cust_password: '',
        }

        this.onChangeCustEmail = this.onChangeCustEmail.bind(this);
        this.onChangeCustPassword = this.onChangeCustPassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

    }

        onChangeCustEmail(e){
            this.setState({
                cust_email: e.target.value
            });
        }

        onChangeCustPassword(e){
            this.setState({
                cust_password: e.target.value
            });
        }

        onChangeCustId(e){
            this.setState({
                cust_id: e.target.value
            });
        }

        onSubmit(e){
            e.preventDefault();

            console.log('Form Submitted');
            console.log('Name: ' + this.state.cust_name);
            console.log('Age: ' + this.state.cust_age);
            console.log('Id: ' + this.state.cust_id);

            const newCust = {
                cust_name: this.state.cust_name,
                cust_age: this.state.cust_age,
                cust_id: this.state.cust_id
            };

            axios.post('http://localhpst:4000/customers/add', newCust).then(res => console.log(res.data));

            this.setState({
                cust_name:'',
                cust_age:'',
                cust_id:''
            });
        }
    render(){
        return (
            <div style={{marginTop: 10}}>
               <h3>Create New Customer</h3>
               <form onSubmit={this.onSubmit}>
                   <div className="form-group">
                       <label>Name: </label>
                       <input  type="text"
                               className="form-control"
                               value={this.state.cust_name}
                               onChange={this.onChangeCustEmail}
                               />
                   </div>
                   <div className="form-group">
                       <label>Age: </label>
                       <input
                               type="text"
                               className="form-control"
                               value={this.state.cust_age}
                               onChange={this.onChangeCustPassword}
                               />
                   </div>

                   <div className="form-group">
                        <label>Id: </label>
                        <input
                            type="text"
                           className="form-control"
                           value={this.state.cust_id}
                           onChange={this.onChangeCustId}
                           />
                   </div>
                   <div className="form-group">
                       <input type="submit" value="Create customer" className="btn btn-primary" />
                   </div>
               </form>
           </div>
        );
    }
}
