import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Button } from 'react-bootstrap';

const Request = props => (
    <tr>
        <td>{props.request.rid}</td>
        <td>{props.request.name}</td>
        <td>{props.request.firstname}</td>
        <td>{props.request.email}</td>
        <td>  <Button variant="primary" >Approve</Button> </td>
    </tr>
)
export default class OtherPage extends  Component {
    constructor(props){
        super(props);
        this.state = {requests: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/request/', {withCredentials: true})
            .then(response => {
                this.setState({ requests: response.data });
            })
            .catch(function (error){
                console.log(error);
            }   )
    }

    policyList() {
        return this.state.requests.map(function(currentRequest, i){
            return (<Request request={currentRequest} key={i} />);
            })
    }

    render(){
        return (
            <div>
                <h3>Requests List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>RID</th>
                            <th>Policy Name</th>
                            <th>Customer Name</th>
                            <th>Customer Email</th>
                            <th>Approve</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.policyList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
