import React from "react";
import { Doughnut } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";
import axios from 'axios';

var dynamic_labels = [];

class ChartsPage extends React.Component {
state = {
  dataDoughnut: {
    labels: dynamic_labels,
    datasets: [
      {
        data: [300, 50, 100, 40, 120],
        backgroundColor: ["#F7464A", "#46BFBD", "#FDB45C", "#949FB1", "#4D5360"],
        hoverBackgroundColor: [
          "#FF5A5E",
          "#5AD3D1",
          "#FFC870",
          "#A8B3C5",
          "#616774"
        ]
      }
    ]
  }
}

componentDidMount() {
    axios.get('http://localhost:4000/ceo/customerbranchcountchart')
        .then(response => {
            this.setState({ data1 : response.data.map(function(current_count,i){
              return (Object.values(current_count))
            })});
            this.state.dataDoughnut.datasets[0].data = this.state.data1.map(function(currentAgent,i){
                return (currentAgent[1])
            });
            dynamic_labels=this.state.data1.map(function(currentAgent,i){
                return (currentAgent[0])
            });
            console.log(this.state.dataDoughnut.labels);
            console.log(dynamic_labels);
          }
        )
        .catch(function (error){
            console.log(error);
        }   )
}

render() {
    return (
    <MDBContainer>
      <h3 className="mt-5">Customers per branch</h3>
      <Doughnut data={this.state.dataDoughnut} options={{ responsive: true }} />
    </MDBContainer>
    );
  }
}

export default ChartsPage;
