import React, { Component } from 'react';

export default class AddCustomer extends  Component {
    constructor(props){
        super(props);

        this.state = {
            cust_name: '',
            cust_age: ''
        }

        this.onChangeCustName = this.onChangeCustName.bind(this);
        this.onChangeCustAge = this.onChangeCustAge.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

    }

        onChangeCustName(e){
            console.log('Setting name to: ' + e.target_value);
            this.setState({
                cust_name: e.target_value
            });
        }

        onChangeCustAge(e){
            this.setState({
                cust_age: e.target_value
            });
        }

        onSubmit(e){
            e.preventDefault();

            console.log('Form Submitted');
            console.log('Name: ' + this.state.cust_name);
            console.log('Age: ' + this.state.cust_age);

            this.setState({
                cust_name:'',
                cust_age:''
            });
        }
    render(){
        return (
            <div style={{marginTop: 10}}>
               <h3>Create New Customer</h3>
               <form onSubmit={this.onSubmit}>
                   <div className="form-group">
                       <label>Name: </label>
                       <input  type="text"
                               className="form-control"
                               value={this.state.cust_name}
                               onChange={this.onChangeCustName}
                               />
                   </div>
                   <div className="form-group">
                       <label>Age: </label>
                       <input
                               type="text"
                               className="form-control"
                               value={this.state.cust_age}
                               onChange={this.onChangeCustAge}
                               />
                   </div>

                   <div className="form-group">
                       <input type="submit" value="Create customer" className="btn btn-primary" />
                   </div>
               </form>
           </div>
        );
    }
}
