import React, { Component } from 'react';
import axios from 'axios';
import {Button} from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Manager = props => (
    <tr>
        <td>{props.manager.mgr_id}</td>
        <td>{props.manager.firstname}</td>
        <td>{props.manager.lastname}</td>
        <td>{props.manager.branch}</td>
        <td>
            <Button variant="danger" onClick={this.deleteInvestigator}>Delete Manager</Button>
        </td>
    </tr>
)
export default class OtherPage extends  Component {
    constructor(props){
        super(props);
        this.state = {managers: []};
    }

    deleteInvestigator(){
        axios.delete('http://localhost:4000/manager/'+this.props.manager.mgr_id, {withCredentials: true})
        .then(response => {
            console.log(response);
            //Display Toast
        })
        .catch(err => {
            console.log(err);
        });
    }

    componentDidMount() {
        axios.get('http://localhost:4000/ceo/managers')
            .then(response => {
                this.setState({ managers: response.data });
            })
            .catch(function (error){
                console.log(error);
            }   )
    }

    managerList() {
        return this.state.managers.map(function(currentManager, i){
            return (<Manager manager={currentManager} key={i} />);
            })
    }

    render(){
        return (
            <div>
                <h3>Managers List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>MGR ID</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Branch</th>
                            <th>Remove Manager</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.managerList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
