import React, { Component } from 'react';
import axios from 'axios';

import { Modal, Button, Dropdown } from 'react-bootstrap';

export default class MakeClaim extends Component {
    constructor(props){
        super(props);

        this.state ={
            showModal: false,
            attributes: []
        }

        this.handleOpen = this.handleOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);

    }

    componentDidMount() {

        axios.get('http://localhost:4000/attribute/'+this.props.pid, {withCredentials: true})
            .then(response => {
                this.setState({attributes: response.data});
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    handleClose(){
        this.setState({
            showModal: false
        });
    }

    handleOpen(){
        this.setState({
            showModal: true
        });
    }

    render(){
        return(
            <div>
            <Button variant="primary" onClick={this.handleOpen}>Make a Claim</Button>
            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered
            show = {this.state.showModal} onHide={this.handleClose}>
            <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Make Claim
            </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <h6>Select an Attribute</h6>
            <p>
            <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
            Select an Attribute
            </Dropdown.Toggle>
            <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </Dropdown.Menu>
            </Dropdown>
            </p>
            </Modal.Body>
            <Modal.Footer>
            Claims may take upto 14 to be settled
            </Modal.Footer>
            </Modal>
            </div>
        );
    }
}
