import React, { Component } from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import Popup from 'reactjs-popup';
import { Button, Form } from 'react-bootstrap';

export default class AddMoneyPopUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
        open: false,
        amount: ''
    };
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.addMoney = this.addMoney.bind(this);
  }
  openModal() {
    this.setState({ open: true });
  }
  closeModal() {
    this.setState({ open: false });
  }
  addMoney() {
      axios.post('http://localhost:4000/customer/addmoney', {addedmoney: this.state.amount}, {withCredentials: true})
      .then(response => {
          this.closeModal();
      })
      .catch(function(err) {
          console.log(err);
      });
  }

  render() {
    return (
      <div>
        <Button variant="primary" onClick={this.openModal}>Add Money</Button>
        <Popup
          open={this.state.open}
          closeOnDocumentClick
          onClose={this.closeModal}>
          <div className="modal">
            <a className="close" onClick={this.closeModal}>
              &times;
            </a>
            <Form>
            <Form.Group controlId="formGroupAmount">
            <Form.Label>Amount</Form.Label>
            <Form.Control type="number" placeholder="Enter amount" />
            </Form.Group>
            </Form>
            <Button variant="primary" onClick={this.addMoney}>Add</Button>
