import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Button } from 'react-bootsrap';
import ViewPolicy from "./viewPolicy.component";

class Policy extends Component {
    constructor(props){
        super(props);

        this.state={
            showModal: false
        };

        this.buyPolicy = this.buyPolicy.bind(this);

    }

    buyPolicy(){
        this.setState(prevState => {showModal: !prevstate.showModal});
    }

    render(){
        return(
            <tr>
                <td>{props.policy.pid}</td>
                <td>{props.policy.name}</td>
                <td>{props.policy.premium}</td>
                <td>{props.policy.duration}</td>
                <td>
                    {this.state.showModal && <ViewPolicy isOpen={true} pid={props.policy.pid}/>}
                    <Button onClick={this.buyPolicy} variant="primary">Buy Policy</Button>
                </td>
            </tr>
        );
    }
}

export default class OtherPage extends  Component {
    constructor(props){
        super(props);
        this.state = {policies: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/policy/', {withCredentials: true})
            .then(response => {
                this.setState({ policies: response.data });
            })
            .catch(function (error){
                console.log(error);
            }   )
    }

    policyList() {
        return this.state.policies.map(function(currentPolicy, i){
            return (<Policy policy={currentPolicy} key={i} />);
            })
    }

    render(){
        return (
            <div>
                <h3>Policies List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>PID</th>
                            <th>Policy Name</th>
                            <th>Premium</th>
                            <th>Duration</th>
                            <th>Buy</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.policyList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
