import React, { Component } from 'react';
import axios from 'axios';

import { Button, Form } from 'react-bootstrap';

export default class editProfile extends  Component {
    constructor(props){
        super(props);

        this.state = {
            firstname: '',
            lastname: '',
            dob: ''
        }

        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeFirstName = this.onChangeFirstName.bind(this);
        this.onChangeLastName = this.onChangeLastName.bind(this);
        this.onChangeDOB = this.onChangeDOB.bind(this);
    }

    componentDidMount(){

        axios.get('http://localhost:4000/customer/profile', {withCredentials: true})
        .then(response => {
            this.setState({
                firstname: response.data[0].firstname,
                lastname: response.data[0].lastname,
                dob: response.data[0].dob
            });
        })
        .catch(function(err) {
            console.log(err);
        });
    }

    onChangeFirstName(e){
        this.setState({
            firstname: e.target.value
        });
    }

    onChangeDOB(e){
        this.setState({
            dob: e.target.value
        });
    }

    onChangeLastName(e){
        this.setState({
            lastname: e.target.value
        });
    }


    onSubmit(e){
        e.preventDefault();
        axios.post('http://localhost:4000/customer/editprofile', {firstname: this.state.firstname, lastname: this.state.lastname, dob: this.state.dob}, {withCredentials: true}).then(response => {
            this.props.history.push("/dashboard");
        }).catch(function(err) {
            console.log(err);
        });
    }

    render(){
        return (
            <div>
                <h3>Edit Profile</h3>
                    <Form>
                      <Form.Group controlId="formFirstName">
                        <Form.Label>First Name</Form.Label>
                        <Form.Control type="text" placeholder={this.state.firstname} onChange={this.onChangeFirstName}/>
                      </Form.Group>
                      <Form.Group controlId="formBasicPassword">
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control type="text" placeholder={this.state.lastname} onChange={this.onChangeLastName}/>
                      </Form.Group>
                      <Form.Group controlId="formBasicPassword">
                        <Form.Label>Date of Birth</Form.Label>
                        <Form.Control type="date" placeholder={this.state.dob} onChange={this.onChangeDOB}/>
                      </Form.Group>
                      <Button variant="primary" type="submit" onChange={this.onSubmit}>
                        Edit Profile
                      </Button>
                    </Form>
            </div>
        );
    }
}
