import React, { Component } from 'react';
import axios from 'axios';

const Attribute = props => (
    <tr>
        <td>{props.attributes.aid}</td>
        <td>{props.attributes.name}</td>
        <td>{props.attributes.amount}</td>
    </tr>
)

export default class ViewPolicy extends Component {

    constructor(props) {
        super(props);

        // this.onChangeTodoDescription = this.onChangeTodoDescription.bind(this);
        // this.onChangeTodoResponsible = this.onChangeTodoResponsible.bind(this);
        // this.onChangeTodoPriority = this.onChangeTodoPriority.bind(this);
        // this.onChangeTodoCompleted = this.onChangeTodoCompleted.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            pid: '',
            name: '',
            premium: '',
            duration: '',
            attributes: []
        }

    }

    componentDidMount() {
        axios.get('http://localhost:4000/policy/'+this.props.match.params.pid)
            .then(response => {
                this.setState({
                    pid: response.data[0].pid,
                    name: response.data[0].name,
                    premium: response.data[0].premium,
                    duration: response.data[0].duration
                })
            })
            .catch(function (error) {
                console.log(error);
            });
        axios.get('http://localhost:4000/attribute/'+this.props.match.params.pid)
            .then(response => {
                this.setState({attributes: response.data});
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    onSubmit(e) {
        // e.preventDefault();
        // const obj = {
        //     pid: this.state.pid,
        //     name: this.state.name,
        //     duration: this.state.duration,
        //     todo_completed: this.state.todo_completed
        // };
        // console.log(obj);
        // axios.post('http://localhost:4000/todos/update/'+this.props.match.params.id, obj)
        //     .then(res => console.log(res.data));

        this.props.history.push('/');
    }

    attributesList(){
        return this.state.attributes.map(function(curAtt, i){
            return (<Attribute attribute={curAtt} key={i} />);
            })
    }

    render() {
        return (
            <div>
                <h3 align="center">Buy Policy</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>PID: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.pid}
                                />
                    </div>
                    <div className="form-group">
                        <label>Name: </label>
                        <input
                                type="text"
                                className="form-control"
                                value={this.state.name}
                                />
                    </div>
                    <div className="form-group">
                        <label>Premium: </label>
                        <input
                                type="text"
                                className="form-control"
                                value={this.state.premium}
                                />
                    </div>
                    <div className="form-group">
                        <label>Duration: </label>
                        <input
                                type="text"
                                className="form-control"
                                value={this.state.duration}
                                />
                    </div>
                    <br />

                    <div className="form-group">
                        <input type="submit" value="Buy Policy" className="btn btn-primary" />
                    </div>
                </form>
                <h3>Attributes List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>AID</th>
                            <th>Attribute Name</th>
                            <th>Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.attributesList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
