import React, {Component} from "react";
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";
import axios from 'axios';

class ChartsPage extends React.Component {
  state = {
    dataBar: {
      labels: ["Life", "Auto", "Cyber", "Travel", "Home"],
      datasets: [
        {
          label: "Number of Policies",
          count_of_policies: [12, 19, 3, 5, 2],
          backgroundColor: [
            "rgba(255, 134,159,0.4)",
            "rgba(98,  182, 239,0.4)",
            "rgba(255, 218, 128,0.4)",
            "rgba(113, 205, 205,0.4)",
            "rgba(170, 128, 252,0.4)"
          ],
          borderWidth: 2,
          borderColor: [
            "rgba(255, 134, 159, 1)",
            "rgba(98,  182, 239, 1)",
            "rgba(255, 218, 128, 1)",
            "rgba(113, 205, 205, 1)",
            "rgba(170, 128, 252, 1)"
          ]
        }
      ]
    },
    barChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        xAxes: [
          {
            barPercentage: 1,
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            }
          }
        ],
        yAxes: [
          {
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            },
            ticks: {
              beginAtZero: true
            }
          }
        ]
      }
    }
  }

  componentDidMount() {
    axios.get('http://localhost:4000/ceo/policychart')
        .then(response => {
            this.setState({ count_of_policies: response.data.map(function(current_count, i){
              return (<int count= {current_count} key = {i}/>)
            }) });
            console.log(this.state.count_of_policies);}
        )
        .catch(function (error){
            console.log(error);
        }   )
}


  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">Policies</h3>
        <Bar data={this.state.dataBar} options={this.state.barChartOptions} />
      </MDBContainer>
    );
  }
}

export default ChartsPage;
